import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Stage = () => {
    const navigate = useNavigate();

    useEffect(() => {
        const checkAdminStatus = async () => {
            try {
                const response = await fetch('http://123.194.1.137:18080/api/v1/user', {
                    method: 'GET',
                    headers: {
                        'Authorization': `Token ${localStorage.getItem('accessToken')}`
                    }
                });

                if (response.ok) {
                    const data = await response.json();
                    if (data.is_admin) {
                        // 如果是管理员，则什么都不做，用户留在Stage页面
                        return;
                    } else {
                        // 如果不是管理员，则导航回首页
                        navigate('/');
                    }
                } else {
                    // 如果响应状态不是ok，则导航回首页
                    navigate('/');
                }
            } catch (error) {
                console.error('Error fetching admin status:', error);
                // 如果发生错误，则导航回首页
                navigate('/');
            }
        };

        checkAdminStatus();
    }, [navigate]);

    return (
        <div>
            <h1>Stage Page</h1>
            <p>Welcome to the stage!</p>
        </div>
    );
};

export default Stage;
