import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Stage = () => {
    const navigate = useNavigate();
    const [activeItem, setActiveItem] = useState('必勝客');
    const [data, setData] = useState([]); // 用于保存从API获取的数据

    useEffect(() => {
        // 在组件加载时检查管理员状态并获取数据
        checkAdminStatus();
        fetchData();
    }, []);

    const checkAdminStatus = async () => {
        try {
            // 检查用户状态的逻辑
        } catch (error) {
            console.error('Error fetching admin status:', error);
            navigate('/');
        }
    };

    const fetchData = async () => {
        const token = localStorage.getItem('accessToken'); // 从localStorage获取token
        if (!token) {
            navigate('/'); // 如果没有token，导航回首页
            return;
        }

        try {
            const response = await fetch('http://123.194.1.137:18080/api/v1/admin/list', {
                method: 'GET',
                headers: {
                    'Authorization': `Token ${token}` // 使用 Token 进行认证
                }
            });
            if (response.ok) {
                const result = await response.json();
                setData(result); // 假设API返回的数据是一个数组
            } else {
                console.error('Error fetching data:', response.statusText);
                navigate('/'); // 如果API返回错误，导航回首页
            }
        } catch (error) {
            console.error('Error fetching data:', error);
            navigate('/'); // 如果发生异常，导航回首页
        }
    };

    const handleItemClick = (item) => {
        setActiveItem(item);
    };

    const handleLogout = () => {
        localStorage.removeItem('accessToken'); // 清除token
        navigate('/'); // 导航回首页
    };

    return (
        <div className='container'>
            <div className='sidebar'>
                <div className='logo'>
                    Pizzahut & KFC<br></br>後台管理系統
                </div>
                <div className='sidebar_pages'>
                    <span
                        className={`t18 ${activeItem === '必勝客' ? 'active' : ''}`}
                        onClick={() => handleItemClick('必勝客')}
                    >
                        必勝客第一階段審核
                    </span>
                    <span
                        className={`t18 ${activeItem === '肯德基' ? 'active' : ''}`}
                        onClick={() => handleItemClick('肯德基')}
                    >
                        肯德基第一階段審核
                    </span>
                </div>
                <div className='logout t18' onClick={handleLogout}>
                    登&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;出
                </div>
            </div>
            <div className='content'>
                {activeItem === '必勝客' ? (
                    <div>
                        <div className='content_header'>
                            <span className='t32'>必勝客第一階段審核</span>
                            <button className='btn-style2 t18'>審核完成</button>
                        </div>
                        <div className='content_main'>
                            <div className="list_header">
                                <span>照片</span>
                                <span>ID</span>
                            </div>
                            <ul>
                                {data.map((item, index) => (
                                    <li key={index}>
                                        <input type="checkbox" id={`item${item.id}`} name={`item${item.id}`} />
                                        <label htmlFor={`item${item.id}`}>
                                            <img src={item.photoUrl} alt="" />
                                        </label>
                                        <span className='t18'>{item.id}</span>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    </div>
                ) : (
                    <div>肯德基相关内容</div>
                )}
            </div>
        </div>
    );
};

export default Stage;
