import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Stage = () => {
    const navigate = useNavigate();
    const [activeItem, setActiveItem] = useState('必勝客'); // 默认为 '必勝客'

    useEffect(() => {
        // 在这里处理初始加载后的操作，例如发送请求检查用户是否是管理员
        checkAdminStatus();
    }, []);

    const checkAdminStatus = async () => {
        try {
            // 检查用户状态的逻辑
        } catch (error) {
            console.error('Error fetching admin status:', error);
            navigate('/');
        }
    };

    const handleItemClick = (item) => {
        setActiveItem(item);
        // 在这里根据点击的项切换内容区域的显示内容
    };

    return (
        <div className='container'>
            <div className='sidebar'>
                <div className='logo'>
                    Pizzahut & KFC<br></br>後台管理系統
                </div>
                <div className='sidebar_pages'>
                    <span
                        className={`t18 ${activeItem === '必勝客' ? 'active' : ''}`}
                        onClick={() => handleItemClick('必勝客')}
                    >
                        必勝客第一階段審核
                    </span>
                    <span
                        className={`t18 ${activeItem === '肯德基' ? 'active' : ''}`}
                        onClick={() => handleItemClick('肯德基')}
                    >
                        肯德基第一階段審核
                    </span>
                </div>
                <div className='logout t18'>
                    登&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;出
                </div>
            </div>
            <div className='content'>
                {activeItem === '必勝客' ? (
                    // 必勝客相关内容
                    <div>必勝客相关内容</div>
                ) : (
                    // 肯德基相关内容
                    <div>肯德基相关内容</div>
                )}
            </div>
        </div>
    );
};

export default Stage;
