import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Stage = () => {
    const navigate = useNavigate();
    const [activeItem, setActiveItem] = useState('必勝客'); 

    useEffect(() => {
        // 在这里处理初始加载后的操作，例如发送请求检查用户是否是管理员
        checkAdminStatus();
    }, []);

    const checkAdminStatus = async () => {
        try {
            // 检查用户状态的逻辑
        } catch (error) {
            console.error('Error fetching admin status:', error);
            navigate('/');
        }
    };

    const handleItemClick = (item) => {
        setActiveItem(item);
        // 在这里根据点击的项切换内容区域的显示内容
    };

    const handleLogout = () => {
        localStorage.removeItem('accessToken'); // 清除token
        navigate('/'); // 导航回首页
    };

    return (
        <div className='container'>
            <div className='sidebar'>
                <div className='logo'>
                    Pizzahut & KFC<br></br>後台管理系統
                </div>
                <div className='sidebar_pages'>
                    <span
                        className={`t18 ${activeItem === '必勝客' ? 'active' : ''}`}
                        onClick={() => handleItemClick('必勝客')}
                    >
                        必勝客第一階段審核
                    </span>
                    <span
                        className={`t18 ${activeItem === '肯德基' ? 'active' : ''}`}
                        onClick={() => handleItemClick('肯德基')}
                    >
                        肯德基第一階段審核
                    </span>
                </div>
                <div className='logout t18' onClick={handleLogout}>
                    登&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;出
                </div>
            </div>
            <div className='content'>
                {activeItem === '必勝客' ? (
                    // 必勝客相关内容
                    <div>
                        <div className='content_header'>
                            <span className='t32'>必勝客第一階段審核</span>
                            <button className='btn-style2 t18'>審核完成</button>
                        </div>
                        <div className='content_main'>
                        <div className="list_header">
                            <span>照片</span>
                            <span>ID</span>
                        </div>
                        <ul>
                            <li>
                                <input type="checkbox" id="item1" name="item1" />
                                <label htmlFor="item1">
                                    <img src="path/to/photo.jpg" alt="照片" />
                                </label>
                                <span>ID: 12345</span>
                            </li>
                            <li>
                                <input type="checkbox" id="item2" name="item2" />
                                <label htmlFor="item2">
                                    <img src="path/to/photo2.jpg" alt="照片" />
                                </label>
                                <span>ID: 67890</span>
                            </li>
                        </ul>
                        </div>
                    </div>
                ) : (
                    // 肯德基相关内容
                    <div>肯德基相关内容</div>
                )}
            </div>
        </div>
    );
};

export default Stage;
