// PizzaHut.js
import React, { useState } from 'react';

const PizzaHut = ({ data }) => {
    const [selectedIds, setSelectedIds] = useState([]);

    const handleCheckboxChange = (id) => {
        setSelectedIds((prevSelectedIds) => {
            if (prevSelectedIds.includes(id)) {
                return prevSelectedIds.filter((selectedId) => selectedId !== id);
            } else {
                return [...prevSelectedIds, id];
            }
        });
    };

    const handleSubmit = async () => {
        const token = localStorage.getItem('accessToken'); // 从localStorage获取token
        if (!token) {
            console.error('No token found');
            return;
        }

        // 创建请求体
        const body = JSON.stringify({ user_id: selectedIds });

        try {
            const response = await fetch('http://123.194.1.137:18080/api/v1/admin/submit_p', {
                method: 'POST',
                headers: {
                    'Authorization': `Token ${token}`,
                    'Content-Type': 'application/json'
                },
                body: body
            });
            if (response.ok) {
                const result = await response.json();
                console.log('Submit result:', result);
            } else {
                const errorResult = await response.json();
                console.error('Error submitting data:', errorResult.msg);
            }
        } catch (error) {
            console.error('Error submitting data:', error);
        }
    };

    return (
        <div>
            <div className='content_header'>
                <span className='t32'>必勝客第一階段審核</span>
                <button className='btn-style2 t18' onClick={handleSubmit}>審核完成</button>
            </div>
            <div className='content_main'>
                <div className="list_header">
                    <span>照片</span>
                    <span>ID</span>
                </div>
                <ul>
                    {data.map((item) => (
                        <li key={item.id}>
                            <input
                                type="checkbox"
                                id={`item${item.id}`}
                                name={`item${item.id}`}
                                onChange={() => handleCheckboxChange(item.id)}
                            />
                            <label htmlFor={`item${item.id}`}>
                                <img src={item.photo} alt="" />
                            </label>
                            <span className='t18'>{item.id}</span>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default PizzaHut;
