import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Stage = () => {
    const navigate = useNavigate();

    useEffect(() => {
        const checkAdminStatus = async () => {
            try {
                const token = localStorage.getItem('accessToken');
                console.log('Access token in Stage component:', token); // 打印accessToken
                if (!token) {
                    console.error('No access token found');
                    navigate('/');
                    return;
                }

                const response = await fetch('http://123.194.1.137:18080/api/v1/user', {
                    method: 'GET',
                    headers: {
                        'Authorization': `Token ${token}`
                    }
                });

                if (response.ok) {
                    const data = await response.json();
                    console.log('Response data:', data);

                    if (data.data && data.data.is_admin) {
                        console.log('User is admin, staying on Stage page');
                    } else {
                        console.log('User is not admin, redirecting to home');
                    }
                } else {
                    console.error('Failed to fetch, redirecting to home');
                    // 如果响应状态不是ok，则导航回首页
                    navigate('/');
                }
            } catch (error) {
                console.error('Error fetching admin status:', error);
                // 如果发生错误，则导航回首页
                navigate('/');
            }
        };

        checkAdminStatus();
    }, [navigate]);

    return (
        <div>
            <div className='sidebar'>
                <div className='logo'>
                Pizzahut & KFC
                </div>
                <div className='sidebar_pages'>
                必勝客第一階段審核
                </div>
                <div className='logout'>
                    登出
                </div>
            </div>
        </div>
    );
};

export default Stage;
